var log=console.log;function countdown(e,n,o,t,r,u,c){var s,a,i=new Date(n,o,t,r,u,c).getTime(),l=document.querySelector(e),d=l.querySelector(".countdown-days-js"),w=l.querySelector(".countdown-hours-js"),f=l.querySelector(".countdown-minutes-js"),m=l.querySelector(".countdown-seconds-js");function y(e){return e<10?"0"+e:e}setInterval(function(){s=(new Date).getTime(),a=i-s,d.innerHTML=y(Math.floor(a/864e5)),w.innerHTML=y(Math.floor(a%864e5/36e5)),f.innerHTML=y(Math.floor(a%36e5/6e4)),m.innerHTML=y(Math.floor(a%6e4/1e3))},1e3)}setTimeout(function(){countdown(containerName=".countdown-1-js",year=2019,month=3,day=25,hours=12,minutes=30,seconds=30)},0);